mapper.select:
select count(*) 
from attraction_info


		sido_code = #{sidoCode}


		gugun_code = #{gugunCode}


		content_type_id = #{contentTypeId}

select (
	content_id, content_type_id, title, addr1, addr2, zipcode, tel, first_image
	first_image2, readcount, sido_code, gugun_code, latitude, longitude, mlevel)
from attraction_info


		sido_code = #{sidoCode}


		gugun_code = #{gugunCode}


		content_type_id = #{contentTypeId}

select (
	content_id, content_type_id, title, addr1, addr2, zipcode, tel, first_image
	first_image2, readcount, sido_code, gugun_code, latitude, longitude, mlevel)
from attraction_info


		sido_code = #{sidoCode}


		gugun_code = #{gugunCode}


		content_type_id = #{contentTypeId}


limit #{start}, {#sizePerPage}

select gugun_code, gugun_name, sido_code
from gugun
where sido_code = #{sidoCode}
